import { WebGLRenderer } from 'three/src/renderers/WebGLRenderer';
import { WebGLProgram } from 'three/src/renderers/webgl/WebGLProgram';
import { WebGLCapabilities } from 'three/src/renderers/webgl/WebGLCapabilities';
import { WebGLExtensions } from 'three/src/renderers/webgl/WebGLExtensions';
import { Material } from 'three/src/materials/Material';
import { Scene } from 'three/src/scenes/Scene';

export class WebGLPrograms {

	constructor( renderer: WebGLRenderer, extensions: WebGLExtensions, capabilities: WebGLCapabilities );

	programs: WebGLProgram[];

	getParameters(
		material: Material,
		lights: any,
		shadows: object[],
		scene: Scene,
		nClipPlanes: number,
		nClipIntersection: number,
		object: any
	): any;
	getProgramCacheKey( parameters: any ): string;
	acquireProgram(
		parameters: any,
		cacheKey: string
	): WebGLProgram;
	releaseProgram( program: WebGLProgram ): void;

}
